import React, { useState, useEffect } from 'react';
import './App.css'; // Make sure to create an App.css file for styles
import './Components/Navbar/Navbar'



// Card component
function Card({ title, imageUrl, date, url }) {
  return (
    <div className="card">
      <div className="card-image">
        <img src={imageUrl} alt={title} />
      </div>
      <div className="card-content">
        <h2 className="card-title">{title}</h2>
        <p className="card-date">{date}</p>
        <a href={url} target="_blank" rel="noopener noreferrer" className="card-link">Read More</a>
      </div>
    </div>
  );
}


// Main App component
function App() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/scrape') // Make sure this matches your Node.js server URL and port
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log(data); // Log to see the data
        setArticles(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);


  return (
    <>
    <Navbar />
    <div className="cards-container">
      {articles.map(article => (
        <Card
          key={article.url}
          title={article.title}
          imageUrl={article.image}
          date={article.published_date}
          url={article.url}
        />
      ))}
    </div>
    </>
  );
}

export default App;
