import React, { useState, useEffect } from 'react';
import './App.css'; // Make sure to create an App.css file for styles
import Navbar from './Components/Navbar/Navbar'
import Card from './Components/Card/Card'


function App() {
  const [articles, setArticles] = useState([]);

  // Helper function to parse and format date
  const parseAndFormatDate = (dateStr) => {
    let parsedDate;
    if (dateStr.includes(',')) {
      // Format: "December 13, 2023"
      parsedDate = new Date(dateStr);
    } else {
      // Format: "Dec 13"
      const currentYear = new Date().getFullYear();
      parsedDate = new Date(`${dateStr} ${currentYear}`);
    }
    return `${parsedDate.getMonth() + 1}.${parsedDate.getDate()}`;
  };

  useEffect(() => {
    fetch('http://localhost:4000/scrape')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const sortedData = data.map(article => ({
          ...article,
          formatted_date: parseAndFormatDate(article.published_date)
        })).sort((a, b) => {
          const dateA = new Date(a.published_date);
          const dateB = new Date(b.published_date);
          return dateB - dateA; // Sort in descending order
        });

        console.log(sortedData); // Log to see the sorted data
        setArticles(sortedData);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <>
      <Navbar />
      <div className="cards-container">
        {articles.map(article => (
          <Card
            key={article.url}
            title={article.title}
            imageUrl={article.image}
            date={article.formatted_date} // Use formatted date
            url={article.url}
          />
        ))}
      </div>
    </>
  );
}

export default App;
